/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `update_product_stock_on_delete`(p_Id_piece_line int,piece_type varchar(255), p_product int, quantity decimal(15,5), IdPiece int, p_warehouse int)
BEGIN
